//https://docs.microsoft.com/pt-br/visualstudio/windows/?view=vs-2019namespace Contas{    public class Contas    {        public int Soma(int a, int b)        {            return a + b;        }        public int Subtracao(int a, int b)        {            return a - b;        }        public int Divisao(int a, int b)        {            return a / b;        }        public int Multiplicacao(int a, int b)        {            return a * b;        }    }}